import React from "react";
import "./App.css";
import AppButtons from "./AppButtons";
import Sleepdeprived from "./sd_logo.png";
import Temp from "./temp_photos.png";
import Temp1 from "./temp_photos1.png";
import Shop from "./Shop";
import About from "./About";
import Contact from "./Contact";
import Terms from "./Terms";
import Tracking from "./Tracking";
import Login from "./Login";
import ForgotLogin from "./ForgotLogin";
import CreateAccount from "./CreateAccount"
import CreateAccountSuccess from "./CreateAccountSuccess"
import Cart from "./components/views/CartPage/Cart";
import Item1 from "./Product/Item1.js"
import Item2 from "./Product/Item2.js"
import Item3 from "./Product/Item3.js"
import Item4 from "./Product/Item4.js"
import {Link} from 'react-router-dom';
//------------- TESTS-----------------------
import TestForm from "./TestForm.js"
import Test from "./Test.js"
import TestCreateAccount from "./TestCreateAccount.js"
//------------------------------------------


import {BrowserRouter as Router, Switch, Route} from "react-router-dom";

/*Currently contains the Search, Currency, logo,
log in, create account, cart, and calls all the buttons
that are in the AppButtons.js file*/
function App() {
  return (

    <Router>
      <div className="app">
        <div className="toppadding">
          <p>
            <span>search | Currency </span>
            <select>
              <option value="usd">USD</option>
              <option value="euro">Euro</option>
              <option value="Yuan">Yuan</option>
              <option value="Yen"> Yen</option>
            </select>
          </p>

          {/*Buttons for login, create account, and cart*/}
          <div className="rightside">
            <Link to='/Login' style={{ textDecoration: 'none' }}>
              <span className="loginbuttondivider">Login</span>
            </Link>
            <span> | </span>
            <Link to='/CreateAccount' style={{ textDecoration: 'none' }}>
              <span className="createdivider">Create Account</span>
            </Link>

            <Link to='/Cart' style={{ textDecoration: 'none' }}>
              <span className="createdivider">Cart</span>
            </Link>
          </div>
          {/*End of code for the login, create account, and cart buttons*/}

          {/*Logo that is located on the top left of the screen*/}
          <div className="logopadding">
            <Link to='/'>
              <img src={Sleepdeprived} alt="Logo" width="350" height="auto"/>
            </Link>
          </div>
          {/*End of code*/}

        </div> {/*End of div for toppadding class*/}

        <AppButtons /> {/*Calls AppButtons.js*/}

        {/*Part of the code for making the all the buttons to go to links*/}
        <Switch>
          <Route path="/" exact component={Home} /> {/*"exact" is there because without it the "/" in all the other routes would display what is in the home page onto that page"*/}
          <Route path="/shop" component={Shop} />
          <Route path="/about" component={About} />
          <Route path="/contact" component={Contact} />
          <Route path="/terms" component={Terms} />
          <Route path="/tracking" component={Tracking} />
          <Route path="/login" component={Login} />
          <Route path="/forgotlogin" component={ForgotLogin} />
          <Route path="/createaccount" component={CreateAccount} />
          <Route path="/cart" component={Cart} />
          <Route path="/item1" component={Item1} />
          <Route path="/item2" component={Item2} />
          <Route path="/item3" component={Item3} />
          <Route path="/item4" component={Item4} />
          <Route path="/createaccountsuccess" component={CreateAccountSuccess} />
          <Route path="/TestForm" component={TestForm} />
          <Route path="/Test" component={Test} />
          <Route path="/TestCreateAccount" component={TestCreateAccount} />

        </Switch>
        {/*End of code*/}

      </div> {/*End div for app class*/}

    </Router>
  );
}

/*What is displayed in the homescreen*/
const Home = () => (
  <div>
    <div className="homeimg">
      <img src={Temp} alt="temp_photo1" />
      <img src={Temp1} alt="temp_photo1" />
    </div>
  </div>
/*End of code*/

);

export default App;
