import React from 'react';
import '../../App.css';
import '../../Products.css';

import { useEffect } from 'react';
import Axios from 'axios'
import { useState } from 'react'
import Detail from './DetailContainer'
import ImageContainer from './ImageContainer'
import { Row, Col } from 'antd';
import { useDispatch } from 'react-redux';

/**/
function ProductPage(props) {


    //const product = ProductData.products.find(x => x._id === '1');
    const dispatch = useDispatch();
    const productId = props.match.params.productId
    const [Product, setProduct] = useState([])
    useEffect(() => {
        Axios.get(`/api/product/products_by_id?id=${productId}&type=single`)
            .then(response => {
                setProduct(response.data[0])
            })

    }, [])

    return (
        <div className="container">
            <div className="app">
                <div className="textpadding">
                    <div className="productformat">
                        <p>{Product.product_name}</p>
                        <b><p>{Product.price}</p></b>
                        <hr />
                        <p>⠀</p>
                        <p>SIZE</p>
                        <p>⠀</p>
                        <p>COLOR</p>
                        <p>⠀</p>
                        <p>QUANTITY</p>
                        <p>⠀</p>
                        <hr />
                        <p>⠀</p>
                        <p>"Added to cart button"</p>
                        <p>⠀</p>
                        <hr />
                        <p>⠀</p>
                        <b><p> Desription: </p></b>
                        <p>⠀</p>
                        <b><p> Material: </p></b>
                    </div>
                </div>
                <div className="productpadding">
                    <ImageContainer detail={Product} />
                </div>
            </div>
        </div>

    )

}

export default ProductPage;
