import React from 'react';
import {useState} from 'react';
import '../../App.css';
import '../../Form.css';


/*Currently contains the Search, Currency, logo,
log in, create account, cart, and calls all the buttons
that are in the AppButtons.js file*/


const Contact = () => {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
  
    const submitRequest = async (e) => {
      e.preventDefault();
      console.log({ email, message });
      const response = await fetch("/acasdasdass", { 
        method: 'POST', 
        headers: { 
            'Content-type': 'application/json'
        }, 
        body: JSON.stringify({email, message}) 
    }); 
      const resData = await response.json(); 
      if (resData.status === 'success'){
        alert("Message Sent."); 
        this.resetForm()
    }else if(resData.status === 'fail'){
        alert("Message failed to send.")
    }
    };
  
    return (
      <div>
        <div className="flex flex-col items-center justify-center bg-gray-200"></div>
        <div className="w-full max-w-sm m-auto flex flex-col my-32">
          <form
            className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 border-gray-200 border"
            onSubmit={submitRequest}
          >
            <h2 className="text-2xl pt-6 pb-10 text-center font-medium text-gray-800">
              Request for early access
            </h2>
            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="Email"
              >
                Your Email
              </label>
              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                type="text"
                name="email"
                placeholder="Email Address"
                onChange={e => setEmail(e.target.value)}
                value={email}
                required
              />
            </div>
            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="message"
              >
                Message For Us
              </label>
              <textarea
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                name="message"
                type="text"
                placeholder="Tell us your purpose"
                onChange={e => setMessage(e.target.value)}
                value={message}
                required
              />
            </div>
            <div className="flex items-center justify-between">
              <button
                className="bg-pink-500 hover:bg-pink-600 text-white font-bold py-2 px-4 mt-6 w-full rounded focus:outline-none focus:shadow-outline"
                type="submit"
              >
                Send A Request
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };


