import React, { useEffect } from "react";
import Product1 from "../../images/hoodie1.png";
import Product2 from "../../images/hoodie2.png";
import Product3 from "../../images/shirt1.png";
import Product4 from "../../images/crewneck1.png";
import "../../App.css";
import '../../Form.css';
import { Link } from "react-router-dom";
import Axios from 'axios';
import { useState } from 'react';
import { Col, Card, Row } from 'antd';
import Icon from '@ant-design/icons';

/*Currently contains the Search, Currency, logo,
log in, create account, cart, and calls all the buttons
that are in the AppButtons.js file*/
function Shop() {

  const [Products, setProducts] = useState([])
  Axios.post('/api/product/getProducts')
    .then(response => {
      if (response.data.success) {
        setProducts(response.data.products)
        console.log(response.data.products)
      } else {
        alert('Failed to fetch product data')
      }
    })
  const renderCards = Products.map((product, index) => {

    return <Col lg={6} md={8} xs={24}>
      <Card
        hoverable={true}
        cover>
      
        <Meta
          title={product.title}
          description={`$${product.price}`}
        />
      </Card>
    </Col>
  })


  return (
    <div className="app">
      <p className="heading">Products</p>
      {Products.length === 0 ?
        <div style={{ display: 'flex', height: '300px', justifyContent: 'center', alignItems: 'center' }}>
          <h2>Loading</h2>
        </div> :
        <div>
          <Row gutter={[16, 16]}>

            {renderCards}

          </Row>
        </div>
      }
      <br />
      <br />
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button>Load More</button>
      </div>

      {/*<Switch>
            <Route path="/About" component={About} />
            <Route path="/item1" component={Item1} />
          </Switch>*/}
      {/*IMPORTANT:
          Dont need this or the <Router> brackets because Shop.js is inside App.js
          If you want to add a new path, add it to the switch bracket in App.js*/}
    </div>
  );
}


export default Shop;
