import React from 'react';
import './App.css';
//import useForm from './useForm';
import './ValidateAccountInfo';
import './Form.css';
import validateAccountInfo from './TestValidate';

/*Currently contains the Search, Currency, logo,
log in, create account, cart, and calls all the buttons
that are in the AppButtons.js file*/

//function CreateAccount(){

//const CreateAccount = ({ submitForm }) => {
//const{handleChange, handleSubmit, values, errors} = useForm(
//submitForm, 
//validate
//);
var errors = {};
class TestCreateAccount extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            firstname: '',
            lastname: '',
            email: '',
            password: '',
            password2: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    validateAccountInfo() {
        //User's FIRST NAME
        if(!this.state.firstname.trim()) {
            errors.firstname = "First Name required"
        }

        //User's LAST NAME
        if(!this.state.lastname.trim()) {
            errors.lastname = "Last Name required"
        }

        //EMAIL
            //if no value/empty showcase "Email Required"
        if(!this.state.email) {
            errors.email = "Email required"
        }
            //if there is input, without proper @ or .com showcase "Email address is invalid"
        else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(this.state.email)) {
            errors.email = "Email address is invalid"
        }

        //PASSWORD
        if(!this.state.password) {
            errors.password = "Password required"
        }
        else if(this.state.password.length < 6) {
            errors.password = "Password needs to be 6 characters or more"
        }

        //CONFIRM PASSWORD
            //User must confirm their password
        if(!this.state.password2) {
            errors.password2 = "You must confirm your password"
        }
            //Checks if user's passwords match
        else if (this.state.password2 !== this.state.password) {
            errors.password2 = "Passwords do not match"
        }

        return errors;
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name; 

        this.setState({
            [name]: value
        })
    }

    handleSubmit(event) {
        //validateAccountInfo();
        //alert (this.state.firstname + ' ' + this.state.lastname + ', your account has been successfully created!');

        event.preventDefault();
    }

    render() {
        return (
            <div className="app">
                <p className="heading">Create Account</p>
                <form className="form" onSubmit={this.handleSubmit} noValidate>
                    <div className="form-inputs">
                        <label htmlFor="firstname" className="form-label1">
                            FIRST NAME
            </label>
                        <input
                            id="firstname"
                            type="text"
                            name="firstname"
                            className="form-input"
                            placeholder="Enter your Full Name"
                            value={this.state.firstname}
                            onChange={this.handleChange}
                        />
                        {errors.firstname && <p>{errors.firstname}</p>}
                    </div>

                    <div className="form-inputs">
                        <label htmlFor="lastname" className="form-label">
                            LAST NAME
            </label>
                        <input
                            id="lastname"
                            type="text"
                            name="lastname"
                            className="form-input"
                            placeholder="Enter your Full Name"
                            value={this.state.lastname}
                            onChange={this.handleChange}
                        />
                        {errors.lastname && <p>{errors.lastname}</p>}
                    </div>

                    <div className="form-inputs">
                        <label htmlFor="email" className="form-label">
                            EMAIL
            </label>
                        <input
                            id="email"
                            type="email"
                            name="email"
                            className="form-input"
                            placeholder="Enter your Email"
                            value={this.state.email}
                            onChange={this.handleChange}
                        />
                        {errors.email && <p>{errors.email}</p>}
                    </div>

                    <div className="form-inputs">
                        <label htmlFor="password" className="form-label">
                            PASSWORD
            </label>
                        <input
                            id="password"
                            type="password"
                            name="password"
                            className="form-input"
                            placeholder="Enter a password"
                            value={this.state.password}
                            onChange={this.handleChange}
                        />
                        {errors.password && <p>{errors.password}</p>}
                    </div>

                    <div className="form-inputs">
                        <label htmlFor="password2" className="form-label">
                            CONFIRM YOUR PASSWORD
            </label>
                        <input
                            id="password2"
                            type="password"
                            name="password2"
                            className="form-input"
                            placeholder="Confirm your password"
                            value={this.state.password2}
                            onChange={this.handleChange}
                        />
                        {errors.password2 && <p>{errors.password2}</p>}
                    </div>

                    <div className="bottom-form">
                        <button className="form-input-btn"
                            type="submit">
                            CREATE
            </button>

                        <div>
                            <span className="form-input-login">
                                Already have an account? Login <a href="/Login">here</a>
                            </span>
                        </div>
                    </div>

                </form>
            </div>
        );
    }

};

export default TestCreateAccount;
